import axios from "axios";
import { useEffect, useState } from "react";
import { useLocation } from "react-router";

let currentPath = "";

const PostTable = (props) => {

    const url = "http://localhost:8080"

    const [postList, setPostList] = useState([]);
    const urlParams = new URLSearchParams(window.location.search)
    let location = useLocation();


    const [page, setPage] = useState(1); // 현재 페이지 수
    const [totalPost, setTotalPost] = useState(0); // 총 게시물 수
    const pageRange = 10; // 페이지당 보여줄 게시물 수
    const btnRange = 5; // 보여질 페이지 버튼의 개수
    const [totalBtnRange, setTotalBtnRange] = useState(0)

    const [currentSet, setCurrentSet] = useState(Math.ceil(page / btnRange));  // 현재 버튼이 몇번째 세트인지 나타내는 수
    
    //const startPage = (currentSet - 1) * btnRange + 1; // 현재 보여질 버튼의 첫번째 수
    const [startPage , setStartPage] = useState((currentSet - 1) * btnRange + 1);
    const endPage = startPage + btnRange - 1; // 현재 보여질 끝 버튼의 수
    
    const [totalSet, setTotalSet] = useState(0);
    //Math.ceil(Math.ceil(totalPost / pageRange) / btnRange); // 전체 벼튼 세트 수

    const startPost = (page - 1) * pageRange + 1; // 시작 게시물 번호
    const endPost = startPost + pageRange - 1; // 끝 게시물 번호

    useEffect(() => {
        console.log(currentSet)
    }, [currentSet])
    useEffect(() => {


        if (props.keyWord.length === 0) {
            axios.get(`${url}/post/list`)
                .then((res) => {
                    setPostList(res.data);
                    setTotalPost(postList.length);
                    setTotalBtnRange(Math.ceil((postList.length) / 10))
                    setTotalSet(Math.ceil(totalPost / pageRange))
                    //setStartPage((currentSet - 1) * btnRange + 1);
                    console.log(startPage);
                })
                .catch((err) => {
                    console.log(err)
                })
        }
        else {
            axios.get(`${url}/post/search?keyWord=${urlParams.get("keyWord")}`)
                .then((res) => {
                    setPostList(res.data);

                })
                .catch((err) => {
                    console.log(err);
                })
        }


    }, [location])

    const trClickEvent = (no) => {

        window.location.href = `/post?no=${no}`;

    }



    return (
        <div class="flex flex-col">
            <div class="overflow-x-auto sm:-mx-6 lg:-mx-8">
                <div class="inline-block min-w-full py-2 sm:px-6 lg:px-8">
                    <div class="overflow-hidden">
                        <table
                            class="min-w-full text-left text-sm font-light text-surface dark:text-white">
                            <thead
                                class="border-b border-neutral-200 font-medium dark:border-white/10">
                                <tr>
                                    <th scope="col" class="px-6 py-4">번호</th>
                                    <th scope="col" class="px-6 py-4">제목</th>
                                    <th scope="col" class="px-6 py-4">작성자</th>
                                </tr>
                            </thead>
                            <tbody>


                                {
                                    postList.slice(startPost - 1, endPost).map((value, index) => {
                                        let nickName = value.userDto.nickName;
                                        return (
                                            <tr class="border-b border-neutral-200 transition duration-300 ease-in-out hover:bg-neutral-100 dark:border-white/10 dark:hover:bg-neutral-600"

                                                key={index} onClick={() => { trClickEvent(value.no) }}>
                                                <td class="whitespace-nowrap px-6 py-4 font-medium" >{value.no}</td>
                                                <td class="whitespace-nowrap px-6 py-4">{value.title}</td>
                                                <td class="whitespace-nowrap px-6 py-4">{nickName}</td>
                                            </tr>
                                        )
                                    })
                                }

                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
            
 
            <ul class="flex justify-center mt-3 font-white-100 ">

                {
                    page > 1 && (
                        <li>
                            <a
                                class="relative block rounded bg-transparent px-3 py-1.5 text-sm text-surface transition duration-300 hover:bg-neutral-300 focus:bg-neutral-100 focus:text-primary-700 focus:outline-none focus:ring-0 active:bg-neutral-100 active:text-primary-700 dark:text-white dark:hover:bg-neutral-700 dark:focus:bg-neutral-700 dark:focus:text-primary-500 dark:active:bg-neutral-700 dark:active:text-primary-500"
                                onClick={() => setPage(page - 1)} $active={false}>
                                prev
                            </a>
                        </li>

                    )}

                {

                    btnRange > totalSet ?

                        Array(totalSet)
                            .fill(startPage)
                            .map((_, i) => {
                                return (
                                    <li>
                                        <a
                                            class="relative block rounded bg-transparent px-3 py-1.5 text-sm text-surface transition duration-300 hover:bg-neutral-300 focus:bg-neutral-100 focus:text-primary-700 focus:outline-none focus:ring-0 active:bg-neutral-100 active:text-primary-700 dark:text-white dark:hover:bg-neutral-700 dark:focus:bg-neutral-700 dark:focus:text-primary-500 dark:active:bg-neutral-700 dark:active:text-primary-500"

                                            key={i}
                                            onClick={() => { setPage(startPage + i) }}
                                        >
                                            {startPage + i}
                                        </a>
                                    </li>
                                );
                            })

                        :
                        
                        Array(btnRange)
                            .fill(startPage)
                            .map((_, i) => {
                                return (
                                    <li>
                                        <a
                                            class="relative block rounded bg-transparent px-3 py-1.5 text-sm text-surface transition duration-300 hover:bg-neutral-300 focus:bg-neutral-100 focus:text-primary-700 focus:outline-none focus:ring-0 active:bg-neutral-100 active:text-primary-700 dark:text-white dark:hover:bg-neutral-700 dark:focus:bg-neutral-700 dark:focus:text-primary-500 dark:active:bg-neutral-700 dark:active:text-primary-500"

                                            key={i}
                                            onClick={() => { setPage(startPage + i) }}
                                        >
                                            {startPage + i}
                                        </a>
                                    </li>
                                );
                            })


                }

                {
                    totalSet > page &&
                    (
                        <li>
                            <a 
                            class="relative block rounded bg-transparent px-3 py-1.5 text-sm text-surface transition duration-300 hover:bg-neutral-300 focus:bg-neutral-100 focus:text-primary-700 focus:outline-none active:bg-neutral-100 active:text-primary-700 dark:text-white dark:hover:bg-neutral-700 dark:focus:bg-neutral-700 dark:focus:text-primary-500 dark:active:bg-neutral-700 dark:active:text-primary-500"
                            onClick={() => setPage(page + 1)} $active={false}>
                                next

                            </a>
                        </li>

                    )}
            </ul>


        </div>
    )
}

export default PostTable;
