import axios from "axios";
import { useEffect, useState } from "react";


const PostTable = () =>{

    const url = "http://localhost:8080"

    const [postList ,setPostList] = useState(['']);


    useEffect( () =>{
        
        axios.get(`${url}/post/list`)
        .then((res) => {
            setPostList(res.data);
        })
        .catch((err) => {
            console.log(err)
        })

    },[])

    const trClickEvent = (no) => {

        window.location.href = `/post?no=${no}`;

    }

    return(
        <table>
            <thead>
                <th>번호</th>
                <th>제목</th>
                <th>작성자</th>
            </thead>
            <tbody>
                    {
                        postList.map((value, index) => {
                            const nickName = value.userDto.nickName;
                            return(
                                <tr key={index} onClick= {() => {trClickEvent(value.no)}}>
                                    <td>{value.no}</td>
                                    <td>{value.title}</td>
                                    <td>{nickName}</td>
                                </tr>
                            )
                        })
                    }
            </tbody>

        </table>
    )
}

export default PostTable;
