import axios from "axios";
import { useEffect, useState } from "react";
import { useLocation } from "react-router";

let currentPath = "";

const PostTable = (props) => {

    const url = "http://localhost:8080"

    const [postList, setPostList] = useState(['']);
    const urlParams = new URLSearchParams(window.location.search)
    let location = useLocation();

    useEffect(() => {

        console.log(props.keyWord)
        
        if (props.keyWord.length === 0) {
            axios.get(`${url}/post/list`)
                .then((res) => {
                    setPostList(res.data);
                    console.log(res)
                })
                .catch((err) => {
                    console.log(err)
                })
        }
        else {
            console.log("else");
            axios.get(`${url}/post/search?keyWord=${urlParams.get("keyWord")}`)
                .then((res) => {
                    setPostList(res.data);

                })
                .catch((err) => {
                    console.log(err);
                })
        }


    }, [location,])

    const trClickEvent = (no) => {

        window.location.href = `/post?no=${no}`;

    }

    return (
        <table>
            <thead>
                <tr>
                    <th>번호</th>
                    <th>제목</th>
                    <th>작성자</th>
                </tr>

            </thead>
            <tbody>
                {
                    postList.map((value, index) => {
                        console.log(value.userDto.nickName)
                        return (
                            <tr key={index} onClick={() => { trClickEvent(value.no) }}>
                                <td>{value.no}</td>
                                <td>{value.title}</td>


                            </tr>
                        )
                    })
                }
            </tbody>

        </table>
    )
}

export default PostTable;
