import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react";
import axios from "axios";
import PostTable from "./PostTable";
import { getCookie } from "../Cookie/Cookie";
import { Link, useLocation } from "react-router-dom";

const Main = () => {
    const url = "http://localhost:8080"

    const dispatch = useDispatch();
    // const number = useSelector((state) => state.counter.number);
    // const nickName = useSelector((state) => state.nickName);
    // const state = useSelector((state) => state)
    const [keyWord, setKeyWord] = useState('');
    const location = useLocation();
    // useEffect(()=>{


    // },[location])

    const test = () => {

        const config = { "Content-Type": 'application/json' };
        let user = {
            'id': 'zxzx8724',
            'password': 'zxzx1235'
        };

        axios.post(`${url}/test`, user, config)
            .then((res) => {

                console.log(res.data)
            })
            .catch((err) => {
                console.log(err)
            })
    }

    const writeButtonEvent = () => {

        if (getCookie("accessToken") === undefined) {
            alert("글쓰기는 로그인 상태에서만 가능합니다.")
            return
        }

        window.location.href = "/write"
    }

    const changekeyWord = (e) => {
        setKeyWord(e.target.value);


    }



    return (
        <div class="p-5">
            {/* <h1>{number}</h1>
            <h2>{nickName}</h2>
            <button onClick={increment}>증가</button>
            <button onClick={decrement}>감소</button>
            <button onClick={test}>테스트</button> */}

            <h1 class="text-2xl md:text-3xl pl-2 my-2 border-l-4  font-sans font-bold border-teal-400  dark:text-gray-200">
                게시글</h1>

            <PostTable keyWord={keyWord}></PostTable>
            <input placeholder="제목 검색" onChange={changekeyWord}></input>
            <Link to={`?keyWord=${keyWord}`} state={{ keyWord: keyWord }}>
                <button>검색</button>
            </Link>

            {/* <button  type = "button" onClick = {writeButtonEvent}>글쓰기</button> */}
            <form class="flex items-center max-w-sm mx-auto">
                <label for="simple-search" class="sr-only">Search</label>
                <div class="relative w-full">
                    <div class="absolute inset-y-0 start-0 flex items-center ps-3 pointer-events-none">
                        <svg class="w-4 h-4 text-gray-500 dark:text-gray-400" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 18 20">
                            <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M3 5v10M3 5a2 2 0 1 0 0-4 2 2 0 0 0 0 4Zm0 10a2 2 0 1 0 0 4 2 2 0 0 0 0-4Zm12 0a2 2 0 1 0 0 4 2 2 0 0 0 0-4Zm0 0V6a3 3 0 0 0-3-3H9m1.5-2-2 2 2 2" />
                        </svg>
                    </div>
                    <input type="text" id="simple-search" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full ps-10 p-2.5  dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="Search branch name..." required />
                </div>
                <button type="submit" class="p-2.5 ms-2 text-sm font-medium text-white bg-blue-700 rounded-lg border border-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">
                    <svg class="w-4 h-4" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 20 20">
                        <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="m19 19-4-4m0-7A7 7 0 1 1 1 8a7 7 0 0 1 14 0Z" />
                    </svg>
                    <span class="sr-only">Search</span>
                </button>
            </form>
        </div>


    )




}




export default Main;
