import React from "react";
import { useState , useEffect } from "react";
import axios
 from "axios";
const SignIn = () =>{

    const [userId,setUserId] = useState('')
    const [password,setPassword] = useState('')
    const [nickName, setNickName] = useState('')
    const [isDuplicate , setIsDuplicate] = useState(false) //중복검사 완료 유무 완료되면 true 안되면 false
    const [isNickNameDuplicate , setisNickNameDuplicate] = useState(false) 
    const url = "http://localhost:8080"
    const config = {"Content-Type": 'application/json'};
    
    //중복검사 함수
    const duplicateCheck = (e) =>{

        e.preventDefault();
        
        const signInForm = document.getElementById("signInForm");

        setUserId(signInForm.elements[0].value);
        let user = {
            'id' : userId
        };

        axios.post(`${url}/duplicateCheck` , user , config)
        .then((res)=>{

        
            
            if(res.data){
                alert('사용 가능한 아이디')
            }
            else{
                alert("중복된 아이디")
            }
        
            setIsDuplicate(res.data);
        })
        .catch((err) => {
            console.log(err)
        })

    }

    const nickNameDuplicateCheck = (e) =>{
        e.preventDefault();
        
        const signInForm = document.getElementById("signInForm");

        //setUserId(signInForm.elements[2].value);
        let user = {
            'nickName' : nickName
        };

        axios.post(`${url}/nickNameDuplicateCheck` , user , config)
        .then((res)=>{

            
            if(res.data){
                alert('사용 가능한 닉네임')
            }
            else{
                alert("중복된 닉네임")
            }
        
            setIsNickNameDuplicate(res.data);
        })
        .catch((err) => {
            console.log(err)
        })

    }

    const SignInEvent = (e) =>{

        e.preventDefault();
        console.log("회원가입 이벤트발생");

        const signInForm = document.getElementById("signInForm");

        setUserId(signInForm.elements[0].value);
        setPassword(signInForm.elements[1].value);
        setNickName(signInForm.elements[2].value);
        
        let user = {
            'id' : userId,
            'password' : password,
            'nickName' : nickName
        };

        console.log(JSON.stringify(user))

        axios.post(`${url}/signIn`, user , config)
        .then((res)=>{
            console.log(res.data);

        })
        .catch((err)=>{
            console.log(err.data);
        })
    }

    const onChangeEvent = (e) =>{
        
        console.log(e.target.id)

        if(e.target.id === "idInput"){
            setUserId(e.target.value);
        }
        else if(e.target.id === "passwordInput"){
            setPassword(e.target.value);
        }
        else if(e.target.id === "nickNameInput"){
            setNickName(e.target.value);
        }

        
    }
    return(
        <div>
            <form id = "signInForm" onSubmit={SignInEvent}> 

                <label>아이디</label>
                <input id ="idInput" onChange={onChangeEvent}></input>
                <button onClick={duplicateCheck}>아이디 중복 확인</button>
                <label>비밀번호</label>
                <input id = "passwordInput" onChange={onChangeEvent}></input>
                <label>닉네임</label>
                <button onClick={nickNameDuplicateCheck}>닉네임 중복 확인</button>
                <input id = "nickNameInput" onChange={onChangeEvent}></input>

                <button type="onSubmit">회원가입</button>
            </form>
        </div>
    )
}

export default SignIn;
