import axios from "axios";
import React, { useState } from "react";
import { getCookie, setCookie } from "../Cookie/Cookie";

const Login = () =>{

    const [userId,setUserId] = useState('')
    const [password,setPassword] = useState('')
    
    const url = "http://localhost:8080"
    const clientUrl = "http://localhost:3000"
    const config = {"Content-Type": 'application/json'};
    
    

    const loginEvent = (e) =>{

        e.preventDefault();

        const loginForm = document.getElementById("loginForm");

        setUserId(loginForm.elements[0].value);
        setPassword(loginForm.elements[1].value);
        
        let user = {
            'id' : userId,
            'password' : password
        };

        console.log(JSON.stringify(user))

        axios.post(`${url}/logIn`, user , {headers : {Authorization: `Bearer ${getCookie("accessToken")}`}}
        )
        .then((res)=>{
            console.log(res.data);

            if(res.data.response === "로그인 성공"){
                console.log(res.data.tokenDto);
                setCookie("accessToken" , `${res.data.tokenDto.accessToken}` , {
                    path : '/',
                    expires : new Date(Date.now() + 3600 * 1000)
                })

                setCookie("refreshToken" ,`${res.data.tokenDto.refreshToken}` , {
                    path : '/',
                    expires : new Date(Date.now() + 3600 * 1000)
                })

                setCookie("key" ,`${res.data.tokenDto.key}` , {
                    path : '/',
                    expires : new Date(Date.now() + 3600 * 1000)
                })
                //window.location.replace('http://localhost:3000')
                
           


            }


        })
        .catch((err)=>{
            console.log(err.data);
        })
    }

    const onChangeEvent = (e) =>{
        

        if(e.target.id === "idInput"){
            setUserId(e.target.value);
        }
        else if(e.target.id === "passwordInput"){
            setPassword(e.target.value);
        }


        
    }

    return(
        <div>
            <form id = "loginForm" onSubmit={loginEvent}> 

                <label>아이디</label>
                <input id = "idInput" onChange={onChangeEvent}></input>
                <label>비밀번호</label>
                <input id = "passwordInput" onChange={onChangeEvent}></input>
                <button>로그인</button>
            </form>
        </div>
    )
}

export default Login;
