import axios from "axios";
import { getCookie } from "./Cookie/Cookie"

const Write = () => {

    const writingRegisterEvent = (e) =>{
        e.preventDefault();
        
        console.log("writingRegisterEvent Activated");

        const title = e.target["title"];
        const content = e.target['content'];
        const url = "http://localhost:3000"
        console.log(title.value, content.value)
        
        const data = {
            title : title,
            content : content
        }

        axios.post(`${url}/write` , data , {
            Headers : {
                'Authorization' : "Bearer " +  getCookie("accessToken"),
                'Content-Type': 'application/json'
            }
        })
        .then((res)=>{
            console.log(res.data)
        })
        .catch((err) =>{
            console.log(err)
        })



    } 

    return(
        <form onSubmit={writingRegisterEvent}>
            <input placeholder="제목" type="text" name = "title"></input>

            <textarea placeholder="내용" name = "content"></textarea>
            <button type="submit">등록</button>
        </form>
    )
}

export default Write;
