import { useEffect } from "react"
import { useState  } from "react"
import { getCookie, removeCookie } from "../Cookie/Cookie"

const Header = () => {

    const [id , setId] = useState('')
    const [nickName , setNickName] = useState('')
    const [isLogIn , setIsLogIn] = useState(false)
    useEffect(() => {

        setId(getCookie("key"))
        setNickName(getCookie("nickName"))

        getCookie("key") === undefined ? setIsLogIn(false) : setIsLogIn(true);

    } , [])


    const logOut = () => {
        removeCookie("key");
        removeCookie("refreshToken");
        removeCookie("accessToken");
    }

    const logInButtonEvent = () =>{
        

        window.location.href = "/logIn"
    }


    return(
        <div>
            <h1>LOGO</h1>
            {/* <h1>{`${nickName}(${id})`}</h1> */}
            {
                isLogIn 
                ? 
                <div>
                    <h1>{`${nickName}(${id})`}</h1><button onClick={logOut}>로그아웃</button>
                </div>
                : 
                <button onClick={logInButtonEvent}>logIn</button>

            }

        </div>
    )
}

export default Header;
