import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react";
import axios from "axios";
import PostTable from "./PostTable";
import { getCookie } from "../Cookie/Cookie";


const Main = () =>{
    const url = "http://localhost:8080"

    const dispatch = useDispatch();
    // const number = useSelector((state) => state.counter.number);
    // const nickName = useSelector((state) => state.nickName);
    // const state = useSelector((state) => state)
    const [searchWord , setSerchWord] = useState(''); 
    const increment = () =>{
        dispatch({type : 'INCREMENT'});
    }

    const decrement = () =>{
        dispatch({type : 'DECREMENT'});
    }
    useEffect(()=>{
        // console.log(number);
        // console.log(nickName)
        // console.log(state);
    },[])

    const test = () =>{
        
        const config = {"Content-Type": 'application/json'};
        let user = {
            'id' : 'zxzx8724',
            'password' : 'zxzx1235'
        };

        axios.post(`${url}/test`, user , config)
        .then( (res) =>{
            
            console.log(res.data)
        })
        .catch((err)=>{
            console.log(err)
        })
    }

    const writeButtonEvent = () =>{

        if(getCookie("accessToken") === undefined){
            alert("글쓰기는 로그인 상태에서만 가능합니다.")
            return
        }

        window.location.href = "/write"
    }

    const changeSearchWord = (e) =>{
        setSerchWord(e.target.value);
        console.log(e.target.value)


    }

    return(
        <div>
            {/* <h1>{number}</h1>
            <h2>{nickName}</h2>
            <button onClick={increment}>증가</button>
            <button onClick={decrement}>감소</button>
            <button onClick={test}>테스트</button> */}
            <h1>글목록</h1>
            <PostTable></PostTable>
            <input placeholder="제목 검색" onChange={changeSearchWord}></input><button>검색</button>

            <button  type = "button" onClick = {writeButtonEvent}>글쓰기</button>
        </div>

        
    )



}




export default Main;
