import axios from "axios";
import { useEffect, useState } from "react";
import { useLocation } from "react-router";

let currentPath = "";

const PostTable = (props) => {

    const url = "http://localhost:8080"

    const [postList, setPostList] = useState(['']);
    const urlParams = new URLSearchParams(window.location.search)
    let location = useLocation();

    // useEffect(() => {
    //     axios.get(url + "/post" + "?" + "no=" + urlParams.get("no")) 
    //     .then((res)=>{
    //         console.log(res.data);
    //         setTitle(res.data.title);
    //         setContent(res.data.content);
    //         setNickName(res.data.userDto.nickName);
    //         setWriterId(res.data.userDto.id);

    //         //게시글의 번호 post.no
    //         setNo(urlParams.get("no"));
    //     })
    //     .catch((err)=>{
    //         console.log(err)
    //     })    


    //     console.log(getCookie("key"));
    //     console.log(writerId);
    // },[])

    useEffect(()=>{

    },[location])

    useEffect(() => {

        console.log(props.keyWord)

        if (urlParams.get("keyWord") === null) {
            axios.get(`${url}/post/list`)
                .then((res) => {
                    setPostList(res.data);
                })
                .catch((err) => {
                    console.log(err)
                })
        }

        else {
            axios.get(`${url}/post/search?keyWord=${urlParams.get("keyWord")}`)
                .then((res) => {
                    console.log(res.data);


                })
                .catch((err) => {
                    console.log(err);
                })
        }

    }, [])

    const trClickEvent = (no) => {

        window.location.href = `/post?no=${no}`;

    }

    return (
        <table>
            <thead>
                <tr>
                    <th>번호</th>
                    <th>제목</th>
                    <th>작성자</th>
                </tr>

            </thead>
            <tbody>
                {
                    postList.map((value, index) => {
                        return (
                            <tr key={index} onClick={() => { trClickEvent(value.no) }}>
                                <td>{value.no}</td>
                                <td>{value.title}</td>
                            </tr>
                        )
                    })
                }
            </tbody>

        </table>
    )
}

export default PostTable;
