import axios from "axios";
import { useState , useEffect} from "react";

const Comment = () =>{
    
    const [commentList , setCommentList] = useState([]);
    const url = "http://localhost:8080"
    const no = new URLSearchParams(window.location.search).get("no")
    
    useEffect(() => {

        axios.get(`${url}/comment/list`)
        .then((res)=>{
            console.log(no)
            setCommentList(res.data);
        })
        .catch(err => {
            console.log(err);
        })
   
    },[])


    return(
        <div>
            <table>
                <thead>
                    <tr>
                        <th>작성자</th>
                        <th>내용</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        commentList.map((value,index)=>{
                            console.log(no === value.postDto.no)
                            return(
                                no === value.postDto.no?
                                <tr key={index}>
                                    <td>{value.userDto?.nickName}</td>
                                    <td>{value.comment}</td>
                                </tr>
                                
                                : <></>
                            )
                        })
                    }
                </tbody>
            </table>
        </div>
    )

}

export default Comment;
