import axios from "axios";
import { getCookie } from "../Cookie/Cookie";
import { useEffect, useState } from "react";
import { useLocation } from 'react-router-dom';

const Write = () => {

    const [title , setTitle] = useState('');
    const [content , setContent] = useState('');
    const [no , setNo] = useState(-1);
    const location = useLocation();

    useEffect( () => {
        console.log(location)
        
        setTitle(location.state.title);
        setContent(location.state.content);
        setNo(location.state.no);
        // setTitle(useLocation.location.state.title);
        // setContent(location.state.content);


    },[]) 


    const checkEvent = (e) => {
        e.preventDefault();
        
        //새로운글 작성시
        if(no === -1){
            writingRegisterEvent(e);
        }

        else{
            editPost(e);
        }
    } 

    const editPost = (e) => {
        const title = e.target["title"].value;
        const content = e.target['content'].value;

        const data = {
            'title' : title,
            'content' : content,
            'no' : no
        }

        
        axios.put(`${url}/write/edit` , data , {
            headers : {
                'Authorization' : "Bearer " +  getCookie("accessToken"),
                'Content-Type': 'application/json'
            }
        })
        .then((res)=>{
            console.log(res.data)
            window.location.href = "/";
        })
        .catch((err) =>{
            console.log(err)
        })
    }

    const writingRegisterEvent = (e) =>{


        console.log("writingRegisterEvent Activated");

        const title = e.target["title"].value;
        const content = e.target['content'].value;
        const url = "http://localhost:3000"
        console.log(title, content)
        
        const data = {
            'title' : title,
            'content' : content,
            'userDto' : null
        }

        axios.post(`${url}/write` , data , {
            headers : {
                'Authorization' : "Bearer " +  getCookie("accessToken"),
                'Content-Type': 'application/json'
            }
        })
        .then((res)=>{
            console.log(res.data)
            window.location.href = "/";
        })
        .catch((err) =>{
            console.log(err)
        })

        

    } 

    return(
        <form onSubmit={writingRegisterEvent}>
            <input placeholder="제목" type="text" name = "title" defaultValue={title}></input>

            <textarea placeholder="내용" name = "content" defaultValue={content}></textarea>
            <button type="submit">등록</button>
        </form>
    )
}

export default Write;
