import axios from "axios";
import { useState , useEffect} from "react";
import { getCookie } from "../../Cookie/Cookie";

const Comment = () =>{
    
    const [commentList , setCommentList] = useState([]);
    const url = "http://localhost:8080"
    const no = new URLSearchParams(window.location.search).get("no")
    
    useEffect(() => {

        axios.get(`${url}/comment/list`)
        .then((res)=>{
            setCommentList(res.data);
            console.log(res.data)
        })
        .catch(err => {
            console.log(err);
        })
   
    },[])

    const deleteComment = (no) =>{
        const checkDelete = window.confirm("댓글을 삭제하시겠습니까?");

        if(checkDelete === true){
            
            axios.delete(`${url}/comment/delete?no=${no}` , {
                headers : {
                    'Authorization' : "Bearer " +  getCookie("accessToken")
                }
            })
            .then((res)=>{
                console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            })
        }

    }

    return(
        <div>
            <table>
                <thead>
                    <tr>
                        <th>작성자</th>
                        <th>내용</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        commentList.map((value,index)=>{
                            console.log(no == value.postDto.no)
                            return(
                                no == value.postDto.no?
                                <tr key={index}>
                                    <td>{value.userDto?.nickName}</td>
                                    <td>{value.comment}</td>
                                    
                                    {
                                        
                                            value.userDto.id === getCookie("key")?
                                            <td><button onClick={() => deleteComment(value.no)}>x</button></td>:
                                            <></> 
                                        
                               
                                    }


                                </tr>
                                
                                : <></>
                            )
                        })
                    }
                </tbody>
            </table>
        </div>
    )

}

export default Comment;
