import axios from "axios";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { getCookie } from "../Cookie/Cookie";
import Comment from "./Comment/Comment";
import { useNavigate } from "react-router";
import { Link } from "react-router-dom";
const Post = () => {

    const url = "http://localhost:8080"
    const [no , setNo] = useState(0);
    const [title , setTitle] = useState('');
    const [content , setContent] = useState('');
    const [writerId , setWriterId] = useState('');
    const [nickName , setNickName] = useState('');
    const id = useSelector((state) => state);
    const urlParams = new URLSearchParams(window.location.search)
    console.log(urlParams.get("no"))

    useEffect(() => {
        axios.get(url + "/post" + "?" + "no=" + urlParams.get("no")) 
        .then((res)=>{
            console.log(res.data);
            setTitle(res.data.title);
            setContent(res.data.content);
            setNickName(res.data.userDto.nickName);
            setWriterId(res.data.userDto.id);

            //게시글의 번호 post.no
            setNo(urlParams.get("no"));
        })
        .catch((err)=>{
            console.log(err)
        })    


        console.log(getCookie("key"));
        console.log(writerId);
    },[])

    const commentSubmit = (e) =>{
        e.preventDefault();


        if(getCookie("accessToken") === undefined){
            alert("댓글 기능은 로그인시 사용가능합니다")
            return;
        }
        
        //let id = useSelector((state) => state);
        //console.log(getCookie("nickName"));
        
        const data = {
            "comment" : e.target["comment"].value,
            "userDto" : null,
            "postDto" : {
                "no" : no
            }
        };

        axios.post(`${url}/comment/write` , data , {
            headers : {
                'Authorization' : "Bearer " +  getCookie("accessToken"),
                'Content-Type': 'application/json'
            }
        })
        .then((res) => {
            console.log(res.data);
        })
        .catch((err) => {
            console.log(err);
        })
    }

    const deletePost = () =>{
        alert("진짜로 삭제하시겠습니까?")
    }


    return(
        <div>
            <h2>{title}</h2>
            <label>작성자</label>
            <p>{nickName}</p>
            <p>{content}</p>
            <br></br>

            {   

                getCookie("key") == writerId?
                <div>
                    <Link to = "/write" state={{title : title , content : content , postNo : no}}><button>수정</button></Link>
                    <button onClick={deletePost}>삭제</button>
                </div>

                :<></>
            }
            
            <h3>댓글</h3>
            <form onSubmit={commentSubmit}>
                <textarea name = "comment" placeholder="댓글 작성"></textarea>
                <button type = "submit">댓글 등록</button>
            </form>
            <Comment no = {no}></Comment>
        </div>
    )

}


export default Post;
